import React, {useEffect, useState} from "react";
import {Button, Card, Col, Image, message, Row, Space, Upload} from "antd";
import {LoadingOutlined, PlusOutlined} from '@ant-design/icons';
import {
    getBase64,
    resizeImage,
    downloadFile,
} from '../utils';

const _iconList = [
    {
        id: 1,
        size: 16,
        label: '16 x 16',
        src: '',
    },
    {
        id: 2,
        size: 32,
        label: '32 x 32',
        src: '',
    },
    {
        id: 3,
        size: 48,
        label: '48 x 48',
        src: '',
    },
    {
        id: 4,
        size: 128,
        label: '128 x 128',
        src: '',
    },
];
const Home = (props) => {
    const [loading, setLoading] = useState(false);
    const [iconList, setIconList] = useState(_iconList);
    const [fileList, setFileList] = useState([]);
    const [resizeFileList, setResizeFileList] = useState([]);

    useEffect(() => {
        if (fileList.length === 0) {
            setIconList(_iconList.map((icon) => {
                return {
                    ...icon,
                    src: '',
                }
            }))
            setResizeFileList([]);
            return;
        }

        Promise.all(_iconList.map((icon) => {
            return resizeImage(fileList[0].originFileObj, icon.size, icon.size)
        })).then((resizedImageList) => {
            // 处理调整后的二进制文件列表
            setResizeFileList(resizedImageList.map((resizedImage) => {
                return {
                    file: resizedImage.file,
                    fileName: `icon_${resizedImage.width}.png`,
                }
            }))
        });

        getBase64(fileList[0].originFileObj, (url) => {
            setLoading(false);
            setIconList(_iconList.map((icon) => {
                return {
                    ...icon,
                    src: url,
                }
            }))
        });

    }, [fileList])


    const beforeUpload = (file) => {
        const isJpgOrPng = file.type === 'image/jpeg' || file.type === 'image/png';
        if (!isJpgOrPng) {
            message.error('You can only upload JPG/PNG file!');
        }
        const isLt2M = file.size / 1024 / 1024 < 1;
        if (!isLt2M) {
            message.error('Image must smaller than 2MB!');
        }

        return false;
    };

    // 当文件变化时
    const onChange = ({fileList: newFileList}) => {
        setFileList(newFileList);
    };

    const onPreview = async (file) => {
        let src = file.url;
        if (!src) {
            src = await new Promise((resolve) => {
                const reader = new FileReader();
                reader.readAsDataURL(file.originFileObj);
                reader.onload = () => resolve(reader.result);
            });
        }
        const image = new Image();
        image.src = src;
        const imgWindow = window.open(src);
        imgWindow?.document.write(image.outerHTML);
    };

    /*#region 上传按钮*/
    const uploadButton = (
        <button
            style={{
                border: 0,
                background: 'none',
            }}
            type="button"
        >
            {loading ? <LoadingOutlined/> : <PlusOutlined/>}
            <div
                style={{
                    marginTop: 8,
                }}
            >
                上传图片
            </div>
        </button>
    );
    /*#endregion*/

    return (
        <>
            <Card
                title="自动裁剪图标工具"
                style={{
                    margin: 20,
                }}
            >

                <Row
                    justify="center"
                    align="bottom"
                    style={{
                        margin: "0 auto",
                        width: "60%",
                    }}
                >
                    {
                        iconList.map((icon) => {
                            return (
                                <Col
                                    key={icon.id}
                                    flex={1}
                                >
                                    <div
                                        style={{
                                            textAlign: "center",
                                        }}
                                    >
                                        <Image
                                            width={icon.size * 3}
                                            height={icon.size * 3}
                                            src={icon.src}
                                            preview={!!icon.src}
                                            fallback="data:image/png;base64,"
                                        />
                                        <p>
                                            {icon.label}
                                        </p>
                                    </div>
                                </Col>
                            )
                        })
                    }
                </Row>

                <Space>
                    <Upload
                        name="avatar"
                        listType="picture-card"
                        accept="image/*"
                        fileList={fileList}
                        action=''
                        beforeUpload={beforeUpload}
                        onPreview={onPreview}
                        onChange={onChange}
                    >
                        {fileList.length >= 1 ? null : uploadButton}
                    </Upload>


                    <Button onClick={() => {
                        resizeFileList.forEach((item) => {
                            console.log(item);
                            downloadFile(item.file, item.fileName)
                        })
                    }}>
                        下载
                    </Button>
                </Space>

            </Card>

        </>
    );
};

export default React.memo(Home);